import React from 'react';

import '../Modal/Modal.css';
import Aux from '../../../hoc/Aux'

const modal = (props) => (
    <div className='Modal'
    style={{
        transform: props.show ? 'translateY(0)' : 'translateY(-100vh)',
        opacity: props.show ? '1' : '0'}}> 

        {props.children}
    </div>

);

export default modal;

